  methods: {
    toAdd () {
      this.$router.push('/portal/category/manage/data/add')
    },
    toLead () {
      const openLoading = this.Loading.startLoading('正在导入...')
      setTimeout(() => {
        openLoading.close()
      }, 1000)
    },
    toDerive () {
      const openLoading = this.Loading.startLoading('正在导出...')
      setTimeout(() => {
        openLoading.close()
      }, 1000)
    },
    search () {
      this.loading = true
      console.log(this.type, this.keyword)
      setTimeout(() => {
        this.loading = false
      }, 2000)
    },
    handleSelectionChange (val) {
      this.multipleSelection = val
    },
    handleEdit (index, studentId) {
      console.log(index, studentId)
      this.$router.push('/portal/category/manage/data/' + studentId)
    },
    handleDelete (index, row) {
      this.list.splice(index, 1)
    },
    handleCurrentChange (val) {
      // console.log(val)
    }
  }
}
